$('#trigger-loading-1').on('click', function(e) {
    $(this).addClass("is-loading");
    setTimeout(function() { $("#trigger-loading-1").removeClass("is-loading");}, 4000);
    e.preventDefault();
});

$('#trigger-loading-2').on('click', function(e) {
    $(this).toggleClass("is-loading");
    setTimeout(function() { $("#trigger-loading-2").removeClass("is-loading");}, 4000);
    e.preventDefault();
});

function showToast() {
    var toastHTML = `<div class="toast fade hide" data-delay="3000">
        <div class="toast-header">
            <i class="anticon anticon-info-circle text-primary m-r-5"></i>
            <strong class="mr-auto">Bootstrap</strong>
            <small>11分钟前</small>
            <button type="button" class="ml-2 close" data-dismiss="toast" aria-label="Close">
                <span aria-hidden="true">&times;</span>
            </button>
        </div>
        <div class="toast-body">
            Hello, world! 这是一个气泡信息.
        </div>
    </div>`

    $('#notification-toast').append(toastHTML)
    $('#notification-toast .toast').toast('show');
    setTimeout(function(){ 
        $('#notification-toast .toast:first-child').remove();
    }, 3000);
}
